mapper.select:
SELECT JR.JOBOFFER_ROOM_IDX, JR.JOBOFFER_ROOM_REGDATE, JR.USER_IDX, JR.COMPANY_USER_IDX, JR.JOBOFFER_ROOM_USER_STATUS, JR.JOBOFFER_ROOM_COMPANY_USER_STATUS, (SELECT UI.USER_SFILE_NAME FROM USER_IMAGE UI WHERE JR.USER_IDX = UI.USER_IDX ) USER_SFILE_NAME, (SELECT CI.COMPANY_SFILE_NAME FROM COMPANY_IMAGE CI WHERE JR.COMPANY_USER_IDX = CI.COMPANY_USER_IDX ) COMPANY_SFILE_NAME, (SELECT MESSAGE_CONTENT FROM JOBOFFER_MESSAGES JM WHERE JR.JOBOFFER_ROOM_IDX = JM.JOB_OFFER_ROOM_IDX ORDER BY JM.MESSAGE_CONTENT_JOB_OFFERS_REGDATE DESC FETCH FIRST 1 ROW ONLY) MESSAGE_CONTENT, (SELECT MESSAGE_CONTENT_JOB_OFFERS_REGDATE FROM JOBOFFER_MESSAGES JM WHERE JM.JOB_OFFER_ROOM_IDX = JR.JOBOFFER_ROOM_IDX ORDER BY MESSAGE_CONTENT_JOB_OFFERS_REGDATE DESC FETCH FIRST 1 ROW ONLY ) MESSAGE_CONTENT_JOB_OFFERS_REGDATE FROM JOBOFFER_ROOMS JR WHERE
COMPANY_USER_IDX = #{company_user_idx} AND JOBOFFER_ROOM_COMPANY_USER_STATUS = #{joboffer_room_company_user_status}
USER_IDX = #{user_idx} AND JOBOFFER_ROOM_USER_STATUS = #{joboffer_room_user_status}
ORDER BY JOBOFFER_ROOM_REGDATE DESC


	 SELECT
    COUNT(*)
FROM
    JOBOFFER_ROOMS    JR
WHERE

		COMPANY_USER_IDX = #{company_user_idx}
AND
    	JOBOFFER_ROOM_COMPANY_USER_STATUS = #{joboffer_room_company_user_status}



	USER_IDX = #{user_idx}
AND
    	JOBOFFER_ROOM_USER_STATUS = #{joboffer_room_user_status}
